import _objectSpread from "@babel/runtime-corejs2/helpers/esm/objectSpread";
import { LOAD_THEMES, UPDATE_SELECTED_THEME } from "../types/theme.types";
const initialThemeState = {
  completeThemes: {},
  lightMode: {},
  darkMode: {},
  selectedTheme: "",
  activeTheme: {}
};

const themeReducer = (state = initialThemeState, action) => {
  switch (action.type) {
    case LOAD_THEMES:
      return _objectSpread({}, state, {
        darkMode: action.payload.darkMode,
        lightMode: action.payload.lightMode,
        completeThemes: action.payload.completeThemes,
        selectedTheme: action.payload.selectedTheme,
        activeTheme: action.payload.completeThemes[action.payload.selectedTheme]
      });

    case UPDATE_SELECTED_THEME:
      return _objectSpread({}, state, {
        selectedTheme: action.payload
      });

    default:
      return state;
  }
};

export default themeReducer;
